import React from 'react'
import Tutorial from '../Tutorial'
import {SiJava} from 'react-icons/si'
const Java1 = () => {
    return (
        <div>
          <Tutorial icons = {<SiJava/>} name = "JAVA" language = "java" next="/Java4" 
          theoryContent = {
                   <p>
                       <h3>Java String<hr/></h3>
                        <p>In Java, a string is a sequence of characters. For example, "hello" is a string containing a sequence of characters 'h', 'e', 'l', 'l', and 'o'.</p>
                       <p>We use double quotes to represent a string in Java. For example,</p>
                       <div class="card card-body">
                            <pre>
                                {`// create a string
String type = "Java programming";
`}
                            </pre>      
                         </div>
    
   
                                
  
                       <h4>Create a String in Java<hr/></h4>
                       
                       <div class="card card-body">
                            <pre>
                                {`class Main {
  public static void main(String[] args) {
    
    // create strings
    String first = "Java";
    String second = "Python";
    String third = "JavaScript";

    // print strings
    System.out.println(first);   // print Java
    System.out.println(second);  // print Python
    System.out.println(third);   // print JavaScript
  }
}
`}
                            </pre>      
                         </div>

                         <h4>Java Parameterized Constructor<hr/></h4>
                         A Java constructor can also accept one or more parameters.
                        Such constructors are known as parameterized constructors (constructor with parameters).


                       <div class="card card-body">

                            <pre>
                                {`class Main {

String languages;

// constructor accepting single value
Main(String lang) {
  languages = lang;
  System.out.println(languages + " Programming Language");
}

public static void main(String[] args) {

  // call constructor by passing a single value
  Main obj1 = new Main("Java");
  Main obj2 = new Main("Python");
  Main obj3 = new Main("C");
}
}
`}
                            </pre>      
                         </div>



                         <h4>Java String Operations<hr/></h4>
                      <h5> 1. Get Length of a String</h5>
                      To find the length of a string, we use the length() method of the String. For example,
                       <div class="card card-body">
                            <pre>
                                {`class Main {
  public static void main(String[] args) {

    // create a string
    String greet = "Hello! World";
    System.out.println("String: " + greet);

    // get the length of greet
    int length = greet.length();
    System.out.println("Length: " + length);
  }
}
`}
                            </pre>      
                         </div>

                         <h5> 2. Join two Strigs</h5>
                      To find the length of a string, we use the length() method of the String. For example,
                       <div class="card card-body">
                            <pre>
                                {`class Main {
  public static void main(String[] args) {

    // create a string
    String greet = "Hello! World";
    System.out.println("String: " + greet);

    // get the length of greet
    int length = greet.length();
    System.out.println("Length: " + length);
  }
}
`}
                            </pre>      
                         </div>

                       
                         
                       
                        
                   </p> 
                }
                />
            
        </div>
    )
}

export default Java1
